  },
  computed: {
    walletAddress: function walletAddress() {
      return Config.walletAddress;
    },
    apiKey: function apiKey() {
      return Config.dapp.apiKey;
    },
    txActionName: function txActionName() {
      return Config.txActionName;
    },
    userName: function userName() {
      return Config.userName;
    }
  },
  mounted: function mounted() {
    this.load();
  },
  methods: {
    load: function load() {
      var _this = this;

      this.axios.get("http://localhost:8081/tx/v1.0/histories?next=0", {
        headers: {
          'Authorization': "Bearer ".concat(this.apiKey),
          'Content-Type': "application/json"
        }
      }).then(function (response) {
        var l = parseInt(_this.database.like.replace(/,/g, ""));
        var p = parseInt(_this.database.people.replace(/,/g, ""));
        var m = parseInt(_this.database.money.replace(/,/g, ""));
        var temp = response.data;
        l = l + temp.likeCount;
        p = p + temp.fundingCount;
        m = m + 10000 * temp.fundingCount;
        l = l.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
        _this.database.like = l;
        p = p.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
        _this.database.people = p;
        m = m.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
        _this.database.money = m;
      }).catch(function (error) {
        alert(error);
      });
    },
    like: function like() {
      var _this2 = this;

      var n = parseInt(this.database.like.replace(/,/g, ""));
      this.axios.post("http://localhost:8081/tx/v1.0/transactions/like", {
        'from': this.walletAddress.pd,
        'inputs': {
          'receiverAddress': this.walletAddress.user,
          'valueAmount': '100000000000000000000'
        }
      }, {
        headers: {
          'api-key': this.apiKey
        }
      }).then(function () {
        alert('좋아요를 눌러주셔서 감사합니다!\n100RWT를 드려요!');
        n = n + 1;
        n = n.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
        _this2.database.like = n;
      }).catch(function (error) {
        alert(error); //alert('좋아요에 실패했습니다!')
      });
    },
    fund: function fund() {
      var _this3 = this;

      var m = parseInt(this.database.money.replace(/,/g, ""));
      var p = parseInt(this.database.people.replace(/,/g, ""));
      this.axios.post("http://localhost:8081/tx/v1.0/transactions/funding", {
        'from': this.walletAddress.pd,
        'inputs': {
          'receiverAddress': this.walletAddress.user,
          'valueAmount': '1000000000000000000000'
        }
      }, {
        headers: {
          'api-key': this.apiKey
        }
      }).then(function () {
        alert("10,000\uC6D0\uC774 \uD380\uB529\uB418\uC5C8\uC2B5\uB2C8\uB2E4.\n1,000RWT\uB97C \uB4DC\uB824\uC694!");
        m = m + 10000;
        m = m.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
        _this3.database.money = m;
        p = p + 1;
        p = p.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",");
        _this3.database.people = p;
      }).catch(function (error) {
        alert(error); //alert('Fund에 실패했습니다!')
      });
    }
  }
};
